@Grab(group = 'net.sf.opencsv', module = 'opencsv', version = '2.3')
import au.com.bytecode.opencsv.CSVReader
import au.com.bytecode.opencsv.CSVParser
import au.com.bytecode.opencsv.CSVWriter

def TEST_FILE_NAME = 'F:\\SoapUI\\TestData\\EmpData2.csv'
def TEST_OUTPUT_FILE_NAME = 'F:\\SoapUI\\TestData\\EmpDataOutput.csv'

List<String[]> rows = new CSVReader(new FileReader(new File(TEST_FILE_NAME)), CSVParser.DEFAULT_SEPARATOR, CSVParser.DEFAULT_ESCAPE_CHARACTER, CSVParser.DEFAULT_QUOTE_CHARACTER, 1).readAll()
log.info "This is row data"+rows


def rowsOver100 = rows.collect  {it.findAll()}


 def sampleStr = "Simple|3456";
 ArrayList samArray = sampleStr.split("\\|")
 log.info "sample Array is ->"+samArray
 log.info "one obj "+samArray[0]

log.info "all ->"+ rowsOver100
log.info "single obj ->"+rowsOver100[0][0]
ArrayList list = rowsOver100[0][0].split("\\|")
log.info "list first ->"+list[0]
log.info "list second ->"+list[1]




File output = new File(TEST_OUTPUT_FILE_NAME)
if (output.exists())
{
    output.delete()
}
output.createNewFile()
output.withWriter { writer ->
    new CSVWriter(writer).writeAll(rowsOver100)

}
